sysauto_script.script:
//Get today's time and date
var rightNow = new GlideDateTime();
// Query the database for NeedIt Task records with Due Date field values older than current time
//Only return NeedIt task record that do not have a State filed value of Closed Complete.
var grNeedIt = new GlideRecord('x_58872_needit_needit_task');
grNeedIt.addQuery('state', '!=', 3);
grNeedIt.addQuery('due_date', '<=', rightNow);
grNeedIt.query();
// Iterate through the results and log the overdue NeedIt task record
while (grNeedIt.next()) {
    gs.info("Overdue NeedIt Task Record= " + grNeedIt.number);
    gs.eventQueue('x_58872_needit.overdueNeedItTask', grNeedIt, grNeedIt.number, gs.getUserName());
}
